import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import { Add as AddIcon, Visibility as VisibilityIcon, ContentCopy as ContentCopyIcon, QrCode as QrCodeIcon } from '@mui/icons-material';
import axios from 'axios';

function StudentList() {
  const [students, setStudents] = useState([]);
  const [open, setOpen] = useState(false);
  const [newStudent, setNewStudent] = useState({ name: '', email: '' });
  const [qrOpen, setQrOpen] = useState(false);
  const [qrValue, setQrValue] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('http://192.168.1.8:5002/api/students', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setStudents(response.data);
    } catch (error) {
      console.error('Errore nel caricamento degli studenti:', error);
    }
  };

  const handleAddStudent = async () => {
    try {
      await axios.post('http://192.168.1.8:5002/api/students', newStudent, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setOpen(false);
      setNewStudent({ name: '', email: '' });
      fetchStudents();
    } catch (error) {
      console.error('Errore nell\'aggiunta dello studente:', error);
    }
  };

  // Funzione per ottenere l'IP locale del server invece di localhost
  function getServerOrigin() {
    // Sostituisci 'localhost' con l'IP locale del server
    const origin = window.location.origin;
    if (origin.includes('localhost')) {
      // Inserisci qui il tuo IP locale, ad esempio:
      return origin.replace('localhost', '192.168.1.8');
    }
    return origin;
  }

  const handleCopyLink = (id) => {
    const url = `${getServerOrigin()}/student/${id}`;
    navigator.clipboard.writeText(url);
    alert('Link tesserino copiato!');
  };

  const handleShowQR = (id) => {
    setQrValue(`${getServerOrigin()}/student/${id}`);
    setQrOpen(true);
  };

  const handleCloseQR = () => {
    setQrOpen(false);
    setQrValue('');
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Lista Studenti
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => setOpen(true)}
          sx={{ mb: 2 }}
        >
          Aggiungi Studente
        </Button>
        <List>
          {students.map((student) => (
            <ListItem key={student._id} divider>
              <ListItemText
                primary={student.name}
                secondary={student.email}
              />
              <ListItemSecondaryAction>
                <IconButton
                  edge="end"
                  onClick={() => navigate(`/student/${student._id}`)}
                >
                  <VisibilityIcon />
                </IconButton>
                <IconButton
                  edge="end"
                  onClick={() => handleCopyLink(student._id)}
                  sx={{ ml: 1 }}
                >
                  <ContentCopyIcon />
                </IconButton>
                <IconButton
                  edge="end"
                  onClick={() => handleShowQR(student._id)}
                  sx={{ ml: 1 }}
                >
                  <QrCodeIcon />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
          ))}
        </List>
      </Paper>

      <Dialog open={open} onClose={() => setOpen(false)}>
        <DialogTitle>Aggiungi Nuovo Studente</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Nome"
            fullWidth
            value={newStudent.name}
            onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Email"
            type="email"
            fullWidth
            value={newStudent.email}
            onChange={(e) => setNewStudent({ ...newStudent, email: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpen(false)}>Annulla</Button>
          <Button onClick={handleAddStudent} variant="contained">
            Aggiungi
          </Button>
        </DialogActions>
      </Dialog>

      <Dialog open={qrOpen} onClose={handleCloseQR}>
        <DialogTitle>QR Code Tesserino</DialogTitle>
        <DialogContent sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          {qrValue && (
            <img src={`https://api.qrserver.com/v1/create-qr-code/?size=180x180&data=${encodeURIComponent(qrValue)}`} alt="QR Code" />
          )}
        </DialogContent>
      </Dialog>
    </Container>
  );
}

export default StudentList;
