import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Tooltip,
  IconButton,
  Snackbar,
} from '@mui/material';
import axios from 'axios';
import PersonIcon from '@mui/icons-material/Person';
import CloseIcon from '@mui/icons-material/Close';
import AppleIcon from '@mui/icons-material/Apple';
import AndroidIcon from '@mui/icons-material/Android';
import ShareIcon from '@mui/icons-material/Share';

function StudentCard() {
  const { id } = useParams();
  const [student, setStudent] = useState(null);
  const [showPwaHint, setShowPwaHint] = useState(() => {
    return localStorage.getItem('hidePwaHint') !== '1';
  });
  const [showUndoAlert, setShowUndoAlert] = useState(false);
  const [undoLessonIndex, setUndoLessonIndex] = useState(null);
  const [showLessonInfo, setShowLessonInfo] = useState(false);
  const [lessonInfo, setLessonInfo] = useState({});

  useEffect(() => {
    fetchStudent();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const fetchStudent = async () => {
    try {
      const response = await axios.get(`http://192.168.1.8:5002/api/students/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setStudent(response.data);
    } catch (error) {
      console.error('Errore nel caricamento dello studente:', error);
    }
  };

  const handleLessonClick = async (lessonIndex) => {
    try {
      await axios.patch(
        `http://192.168.1.8:5002/api/students/${id}/lessons/${lessonIndex}`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      fetchStudent();
    } catch (error) {
      console.error('Errore nell\'aggiornamento della lezione:', error);
    }
  };

  // Funzione per annullare una lezione
  const handleUndoLesson = async (lessonIndex) => {
    try {
      await axios.patch(
        `http://192.168.1.8:5002/api/students/${id}/lessons/${lessonIndex}/undo`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      fetchStudent();
      setUndoLessonIndex(lessonIndex);
      setShowUndoAlert(true);
    } catch (error) {
      alert('Errore nell\'annullamento della lezione');
    }
  };

  const handleClosePwaHint = () => {
    setShowPwaHint(false);
    localStorage.setItem('hidePwaHint', '1');
  };

  // Gestione click su modulo disponibile (ma annullato)
  const handleAvailableLessonClick = (lessonIndex, lesson) => {
    if (lesson.date) {
      setLessonInfo({
        number: lessonIndex + 1,
        date: new Date(lesson.date).toLocaleString('it-IT', {
          day: '2-digit', month: '2-digit', year: '2-digit', hour: '2-digit', minute: '2-digit'
        })
      });
      setShowLessonInfo(true);
    } else {
      // Niente banner se mai usata
    }
  };

  if (!student) {
    return (
      <Container>
        <Typography>Caricamento...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="sm" sx={{
      p: 0, m: 0, minWidth: '100vw', minHeight: '100vh',
      display: 'flex', alignItems: 'center', justifyContent: 'center',
      bgcolor: '#fff8e1',
    }}>
      {/* Popup istruzioni PWA */}
      {showPwaHint && (
        <Box sx={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100vw',
          height: '100vh',
          bgcolor: 'rgba(0,0,0,0.45)',
          zIndex: 9999,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}>
          <Box sx={{
            bgcolor: '#fff8e1',
            borderRadius: 6,
            boxShadow: '0 8px 32px #ff980088',
            p: 4,
            maxWidth: 370,
            width: '92vw',
            textAlign: 'center',
            position: 'relative',
            border: '4px solid #ff9800',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}>
            <IconButton onClick={handleClosePwaHint} sx={{ position: 'absolute', top: 8, right: 8 }}>
              <CloseIcon />
            </IconButton>
            <Typography variant="h5" sx={{ mb: 2, fontWeight: 900, color: '#d84315', letterSpacing: 2, fontFamily: 'Oswald, Impact, Arial, sans-serif', textShadow: '0 2px 8px #ff980088' }}>
              Installa il tesserino come App!
            </Typography>
            <Typography variant="body1" sx={{ mb: 3, color: '#795548', fontWeight: 700, fontSize: 18 }}>
              Per un accesso rapido e senza limiti, aggiungi il tesserino alla schermata Home:
            </Typography>
            <Box sx={{ textAlign: 'left', mb: 2, width: '100%' }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <AppleIcon sx={{ color: '#222', fontSize: 28, mr: 1 }} />
                <Typography variant="subtitle2" sx={{ fontWeight: 700, color: '#ff9800', fontSize: 18 }}>iPhone (Safari):</Typography>
              </Box>
              <Typography variant="body2" sx={{ mb: 1, ml: 4, color: '#333', fontWeight: 600 }}>
                1. Tocca <ShareIcon sx={{ fontSize: 18, mb: '-3px', color: '#1976d2' }} /> <b>Condividi</b> in basso<br/>
                2. Scegli <b>Aggiungi a Home</b>
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1, mt: 2 }}>
                <AndroidIcon sx={{ color: '#388e3c', fontSize: 28, mr: 1 }} />
                <Typography variant="subtitle2" sx={{ fontWeight: 700, color: '#388e3c', fontSize: 18 }}>Android (Chrome):</Typography>
              </Box>
              <Typography variant="body2" sx={{ mb: 1, ml: 4, color: '#333', fontWeight: 600 }}>
                1. Tocca <b>⋮</b> in alto a destra<br/>
                2. Scegli <b>Aggiungi a schermata Home</b>
              </Typography>
            </Box>
            <Typography variant="caption" sx={{ color: '#d84315', fontWeight: 700, letterSpacing: 1 }}>
              Vedrai l'icona "MY BOXING LESSONS" tra le tue app!
            </Typography>
          </Box>
        </Box>
      )}
      <Box
        sx={{
          width: { xs: '98vw', sm: 340, md: 360 },
          maxWidth: { xs: '98vw', sm: 400 },
          height: { xs: '98vh', sm: 500 },
          maxHeight: { xs: '98vh', sm: 600 },
          minHeight: 420,
          minWidth: 260,
          m: 0,
          p: 0,
          borderRadius: 8,
          boxShadow: '0 8px 32px #0007',
          position: 'relative',
          overflow: 'hidden',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-end',
          background: 'linear-gradient(135deg, #e3f2fd 0%, #fff 100%)',
          border: '4px solid',
          borderImage: 'linear-gradient(135deg, #1976d2 0%, #00c6ff 100%) 1',
        }}
      >
        {/* Sfondo immagine */}
        <img
          src="/main.jpg"
          alt="Boxe BG"
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            zIndex: 0,
            border: '4px solid #b71c1c',
            borderRadius: '8px',
          }}
        />
        {/* Overlay scuro per contrasto */}
        <Box sx={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          bgcolor: 'rgba(0,0,0,0.18)',
          zIndex: 2,
        }} />
        {/* Nome studente sopra la fila dei moduli, centrato e più in basso */}
        <Box sx={{
          position: 'absolute',
          left: 0,
          bottom: 70,
          width: '100%',
          zIndex: 5,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: 0.7,
          minHeight: 38,
        }}>
          <PersonIcon sx={{ color: '#fffde7', fontSize: 22, mb: '1px' }} />
          <Typography
            align="center"
            sx={{
              fontFamily: "'Oswald', Impact, Arial, sans-serif",
              fontWeight: 900,
              letterSpacing: 2,
              color: '#fffde7',
              textShadow: '0 2px 8px #222b, 0 0 6px #222b',
              WebkitTextStroke: '1.2px #222',
              m: 0,
              fontSize: 26,
              textTransform: 'uppercase',
              lineHeight: 1.1,
              px: 1.5,
              borderRadius: 3,
              background: 'rgba(67,160,71,0.12)',
              boxShadow: '0 1px 4px #2222',
            }}
          >
            {student.name}
          </Typography>
        </Box>
        {/* Badge lezioni: una sola fila da 10, rettangolari verticali */}
        <Box sx={{
          position: 'absolute',
          left: 0,
          bottom: 28,
          width: '100%',
          zIndex: 4,
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'center',
          gap: 0.5,
        }}>
          {student.lessons.slice(0, 10).map((lesson, lessonIndex) => {
            const dateString = lesson.date ? new Date(lesson.date).toLocaleString('it-IT', { day: '2-digit', month: '2-digit', year: '2-digit', hour: '2-digit', minute: '2-digit' }) : '';
            return lesson.isUsed ? (
              <Tooltip key={lessonIndex} title={dateString ? `Lezione effettuata il ${dateString}` : ''} arrow>
                <Box
                  sx={{
                    width: 38,
                    height: 52,
                    borderRadius: 6,
                    bgcolor: 'linear-gradient(135deg, #bdbdbd 0%, #e0e0e0 100%)',
                    border: '2.5px solid #aaa',
                    boxShadow: 'none',
                    opacity: 0.92,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontWeight: 700,
                    fontSize: 18,
                    color: '#d32f2f',
                    mb: 0,
                    transition: 'all 0.2s',
                    boxSizing: 'border-box',
                    cursor: 'pointer',
                    position: 'relative',
                  }}
                  onClick={() => {}}
                >
                  <Box sx={{ background: 'orange', borderRadius: 3, px: 0.7, py: 0.2, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                    ✗
                  </Box>
                  {/* Bottone Annulla visibile solo se token presente (admin) */}
                  {localStorage.getItem('token') && (
                    <button
                      onClick={() => handleUndoLesson(lessonIndex)}
                      style={{
                        position: 'absolute',
                        top: 2,
                        right: 2,
                        width: 18,
                        height: 18,
                        background: '#ff9800',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '50%',
                        fontSize: 10,
                        fontWeight: 700,
                        cursor: 'pointer',
                        boxShadow: '0 1px 4px #e6510022',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        zIndex: 10,
                      }}
                      title="Annulla lezione"
                    >
                      ↺
                    </button>
                  )}
                </Box>
              </Tooltip>
            ) : (
              <Box
                key={lessonIndex}
                sx={{
                  width: 38,
                  height: 52,
                  borderRadius: 6,
                  bgcolor: 'linear-gradient(135deg, #fff 0%, #e3f2fd 100%)',
                  border: '2.5px solid #43a047',
                  boxShadow: '0 2px 8px #1976d233',
                  opacity: 0.92,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  fontWeight: 700,
                  fontSize: 18,
                  color: '#1976d2',
                  mb: 0,
                  transition: 'all 0.2s',
                  boxSizing: 'border-box',
                  '&:hover': {
                    boxShadow: '0 0 8px #43a047',
                    transform: 'scale(1.08)',
                    background: 'linear-gradient(135deg, #bbdefb 0%, #90caf9 100%)',
                  },
                }}
                onClick={() => handleAvailableLessonClick(lessonIndex, lesson)}
              >
                <Box sx={{ background: 'orange', borderRadius: 3, px: 0.7, py: 0.2, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  {lessonIndex + 1}
                </Box>
              </Box>
            );
          })}
        </Box>
      </Box>
      {/* Snackbar avviso annullamento modulo */}
      <Snackbar
        open={showUndoAlert && !localStorage.getItem('token')}
        autoHideDuration={4000}
        onClose={() => setShowUndoAlert(false)}
        message={`Una lezione è stata annullata dall'amministratore. Ora puoi riutilizzare il modulo n°${undoLessonIndex !== null ? undoLessonIndex + 1 : ''}.`}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        sx={{
          '& .MuiSnackbarContent-root': {
            bgcolor: '#ff9800',
            color: '#fff',
            fontWeight: 700,
            fontSize: 16,
            borderRadius: 3,
            boxShadow: '0 2px 8px #d8431555',
            letterSpacing: 1,
          }
        }}
      />
      {/* Snackbar info lezione annullata (clic su modulo disponibile con data) */}
      <Snackbar
        open={showLessonInfo}
        autoHideDuration={4000}
        onClose={() => setShowLessonInfo(false)}
        message={lessonInfo.number ? `Modulo n°${lessonInfo.number}: lezione effettuata il ${lessonInfo.date}` : ''}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        sx={{
          '& .MuiSnackbarContent-root': {
            bgcolor: '#1976d2',
            color: '#fff',
            fontWeight: 700,
            fontSize: 16,
            borderRadius: 3,
            boxShadow: '0 2px 8px #1976d255',
            letterSpacing: 1,
          }
        }}
      />
    </Container>
  );
}

export default StudentCard;
