import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Box,
  Tooltip,
} from '@mui/material';
import axios from 'axios';
import PersonIcon from '@mui/icons-material/Person';

function StudentCard() {
  const { id } = useParams();
  const [student, setStudent] = useState(null);

  useEffect(() => {
    fetchStudent();
  }, [id]);

  const fetchStudent = async () => {
    try {
      const response = await axios.get(`http://192.168.1.8:5001/api/students/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setStudent(response.data);
    } catch (error) {
      console.error('Errore nel caricamento dello studente:', error);
    }
  };

  const handleLessonClick = async (lessonIndex) => {
    try {
      await axios.patch(
        `http://192.168.1.8:5001/api/students/${id}/lessons/${lessonIndex}`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      fetchStudent();
    } catch (error) {
      console.error('Errore nell\'aggiornamento della lezione:', error);
    }
  };

  if (!student) {
    return (
      <Container>
        <Typography>Caricamento...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="sm" sx={{ mt: 4, display: 'flex', justifyContent: 'center' }}>
      <Box
        sx={{
          width: { xs: '98vw', sm: 340, md: 360 },
          maxWidth: 400,
          height: { xs: '90vh', sm: 500 },
          maxHeight: 600,
          minHeight: 420,
          minWidth: 260,
          m: { xs: 0.5, sm: 0 },
          p: 0,
          borderRadius: 8,
          boxShadow: '0 8px 32px #0007',
          position: 'relative',
          overflow: 'hidden',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-end',
          background: 'linear-gradient(135deg, #e3f2fd 0%, #fff 100%)',
          border: '4px solid',
          borderImage: 'linear-gradient(135deg, #1976d2 0%, #00c6ff 100%) 1',
        }}
      >
        {/* Sfondo immagine */}
        <img
          src="/main.jpg"
          alt="Boxe BG"
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            zIndex: 0,
            border: '4px solid #b71c1c',
            borderRadius: '8px',
          }}
        />
        {/* Overlay scuro per contrasto */}
        <Box sx={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          bgcolor: 'rgba(0,0,0,0.18)',
          zIndex: 2,
        }} />
        {/* Nome studente sopra la fila dei moduli, centrato e più in basso */}
        <Box sx={{
          position: 'absolute',
          left: 0,
          bottom: 70,
          width: '100%',
          zIndex: 5,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: 0.7,
          minHeight: 38,
        }}>
          <PersonIcon sx={{ color: '#fffde7', fontSize: 22, mb: '1px' }} />
          <Typography
            align="center"
            sx={{
              fontFamily: "'Oswald', Impact, Arial, sans-serif",
              fontWeight: 900,
              letterSpacing: 2,
              color: '#fffde7',
              textShadow: '0 2px 8px #222b, 0 0 6px #222b',
              WebkitTextStroke: '1.2px #222',
              m: 0,
              fontSize: 26,
              textTransform: 'uppercase',
              lineHeight: 1.1,
              letterSpacing: 2,
              px: 1.5,
              borderRadius: 3,
              background: 'rgba(67,160,71,0.12)',
              boxShadow: '0 1px 4px #2222',
            }}
          >
            {student.name}
          </Typography>
        </Box>
        {/* Badge lezioni: una sola fila da 10, rettangolari verticali */}
        <Box sx={{
          position: 'absolute',
          left: 0,
          bottom: 10,
          width: '100%',
          zIndex: 4,
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'center',
          gap: 1.2,
        }}>
          {student.lessons.slice(0, 10).map((lesson, lessonIndex) => {
            const dateString = lesson.date ? new Date(lesson.date).toLocaleString('it-IT', { day: '2-digit', month: '2-digit', year: '2-digit', hour: '2-digit', minute: '2-digit' }) : '';
            return lesson.isUsed ? (
              <Tooltip key={lessonIndex} title={dateString ? `Lezione effettuata il ${dateString}` : ''} arrow>
                <Box
                  sx={{
                    width: 28,
                    height: 44,
                    borderRadius: 6,
                    bgcolor: 'linear-gradient(135deg, #bdbdbd 0%, #e0e0e0 100%)',
                    border: '2.5px solid #aaa',
                    boxShadow: 'none',
                    opacity: 0.92,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontWeight: 700,
                    fontSize: 18,
                    color: '#d32f2f',
                    mb: 0,
                    transition: 'all 0.2s',
                    boxSizing: 'border-box',
                    cursor: 'pointer',
                  }}
                  onClick={() => {}}
                >
                  ✗
                </Box>
              </Tooltip>
            ) : (
              <Box
                key={lessonIndex}
                sx={{
                  width: 28,
                  height: 44,
                  borderRadius: 6,
                  bgcolor: 'linear-gradient(135deg, #fff 0%, #e3f2fd 100%)',
                  border: '2.5px solid #43a047',
                  boxShadow: '0 2px 8px #1976d233',
                  opacity: 0.92,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  fontWeight: 700,
                  fontSize: 18,
                  color: '#1976d2',
                  mb: 0,
                  transition: 'all 0.2s',
                  boxSizing: 'border-box',
                  '&:hover': {
                    boxShadow: '0 0 8px #43a047',
                    transform: 'scale(1.08)',
                    background: 'linear-gradient(135deg, #bbdefb 0%, #90caf9 100%)',
                  },
                }}
                onClick={() => handleLessonClick(lessonIndex)}
              >
                {lessonIndex + 1}
              </Box>
            );
          })}
        </Box>
      </Box>
    </Container>
  );
}

export default StudentCard;
