import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Box,
} from '@mui/material';
import axios from 'axios';
import PersonIcon from '@mui/icons-material/Person';

function StudentCard() {
  const { id } = useParams();
  const [student, setStudent] = useState(null);

  useEffect(() => {
    fetchStudent();
  }, [id]);

  const fetchStudent = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/students/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setStudent(response.data);
    } catch (error) {
      console.error('Errore nel caricamento dello studente:', error);
    }
  };

  const handleLessonClick = async (lessonIndex) => {
    try {
      await axios.patch(
        `http://localhost:5001/api/students/${id}/lessons/${lessonIndex}`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      fetchStudent();
    } catch (error) {
      console.error('Errore nell\'aggiornamento della lezione:', error);
    }
  };

  if (!student) {
    return (
      <Container>
        <Typography>Caricamento...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="sm" sx={{ mt: 4, display: 'flex', justifyContent: 'center' }}>
      <Box
        sx={{
          width: 320,
          height: 500,
          borderRadius: 8,
          boxShadow: '0 8px 32px #0007',
          position: 'relative',
          overflow: 'hidden',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-end',
          p: 0,
          m: 0,
          background: 'linear-gradient(135deg, #e3f2fd 0%, #fff 100%)',
          border: '4px solid',
          borderImage: 'linear-gradient(135deg, #1976d2 0%, #00c6ff 100%) 1',
        }}
      >
        {/* Sfondo immagine */}
        <img
          src="/main.jpg"
          alt="Boxe BG"
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            zIndex: 0,
          }}
        />
        {/* Overlay scuro per contrasto */}
        <Box sx={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          bgcolor: 'rgba(0,0,0,0.18)',
          zIndex: 2,
        }} />
        {/* Badge lezioni: due righe da 5 in basso sopra il nome */}
        <Box sx={{
          position: 'absolute',
          left: 0,
          bottom: 56,
          width: '100%',
          zIndex: 4,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: 0.3,
        }}>
          {[0, 1].map(row => (
            <Box key={row} sx={{ display: 'flex', flexDirection: 'row', gap: 0.7, justifyContent: 'center' }}>
              {student.lessons.slice(row * 5, row * 5 + 5).map((lesson, index) => (
                <Box
                  key={row * 5 + index}
                  sx={{
                    width: 28,
                    height: 28,
                    borderRadius: '50%',
                    bgcolor: lesson.isUsed ? 'linear-gradient(135deg, #bdbdbd 0%, #e0e0e0 100%)' : 'linear-gradient(135deg, #fff 0%, #e3f2fd 100%)',
                    border: lesson.isUsed ? '2px solid #aaa' : '2px solid #1976d2',
                    boxShadow: lesson.isUsed ? 'none' : '0 2px 8px #1976d233',
                    opacity: 0.92,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: lesson.isUsed ? 'default' : 'pointer',
                    fontWeight: 700,
                    fontSize: 13,
                    color: lesson.isUsed ? '#888' : '#1976d2',
                    mb: 0,
                    transition: 'all 0.2s',
                    boxSizing: 'border-box',
                    '&:hover': !lesson.isUsed ? {
                      boxShadow: '0 0 8px #1976d2',
                      transform: 'scale(1.08)',
                      background: 'linear-gradient(135deg, #bbdefb 0%, #90caf9 100%)',
                    } : {},
                  }}
                  onClick={() => !lesson.isUsed && handleLessonClick(row * 5 + index)}
                >
                  {row * 5 + index + 1}
                </Box>
              ))}
            </Box>
          ))}
        </Box>
        {/* Nome studente in basso su barra bianca arrotondata, compatto e allineato, con icona */}
        <Box sx={{
          position: 'absolute',
          left: 0,
          bottom: 0,
          width: '100%',
          bgcolor: 'rgba(255,255,255,0.97)',
          py: 0.2,
          zIndex: 5,
          borderTopLeftRadius: 32,
          borderTopRightRadius: 32,
          boxShadow: '0 -2px 12px #0002',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          gap: 1,
        }}>
          <PersonIcon sx={{ color: '#1976d2', fontSize: 20, mb: '2px' }} />
          <Typography
            variant="subtitle2"
            align="center"
            sx={{
              fontFamily: "'Oswald', Impact, Arial, sans-serif",
              fontWeight: 700,
              letterSpacing: 1,
              color: '#1976d2',
              textShadow: '1px 1px 2px #fff',
              m: 0,
              fontSize: 14,
            }}
          >
            {student.name}
          </Typography>
        </Box>
      </Box>
    </Container>
  );
}

export default StudentCard;
