import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Box,
} from '@mui/material';
import axios from 'axios';

function StudentCard() {
  const { id } = useParams();
  const [student, setStudent] = useState(null);

  useEffect(() => {
    fetchStudent();
  }, [id]);

  const fetchStudent = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/students/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setStudent(response.data);
    } catch (error) {
      console.error('Errore nel caricamento dello studente:', error);
    }
  };

  const handleLessonClick = async (lessonIndex) => {
    try {
      await axios.patch(
        `http://localhost:5001/api/students/${id}/lessons/${lessonIndex}`,
        {},
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      fetchStudent();
    } catch (error) {
      console.error('Errore nell\'aggiornamento della lezione:', error);
    }
  };

  if (!student) {
    return (
      <Container>
        <Typography>Caricamento...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="sm" sx={{ mt: 4 }}>
      <Paper
        elevation={6}
        sx={{
          p: 3,
          border: '4px solid #1976d2',
          borderRadius: 4,
          position: 'relative',
          background: 'rgba(255,255,255,0.95)',
          overflow: 'hidden',
          minHeight: 500,
        }}
      >
        {/* Sfondo boxe */}
        <img
          src="/main.jpg"
          alt="Boxe BG"
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            zIndex: 0,
          }}
        />
        <Typography
          variant="h4"
          align="center"
          gutterBottom
          sx={{
            fontFamily: "'Oswald', Impact, Arial, sans-serif",
            fontWeight: 700,
            letterSpacing: 2,
            color: '#222',
            zIndex: 1,
            position: 'relative',
            textShadow: '1px 1px 2px #fff, 0 0 10px #1976d2',
          }}
        >
          {student.name}
        </Typography>
        <Typography
          variant="subtitle1"
          align="center"
          color="textSecondary"
          gutterBottom
          sx={{ zIndex: 1, position: 'relative' }}
        >
          {student.email}
        </Typography>
        <Grid container spacing={1} sx={{ mt: 2, zIndex: 1, position: 'relative', mb: 6 }}>
          {[0, 1].map(col => (
            <Grid item xs={6} key={col}>
              {student.lessons.slice(col * 5, col * 5 + 5).map((lesson, index) => (
                <Card
                  key={col * 5 + index}
                  sx={{
                    bgcolor: lesson.isUsed ? '#e0e0e0' : 'white',
                    cursor: lesson.isUsed ? 'default' : 'pointer',
                    border: lesson.isUsed ? '2px solid #aaa' : '2px solid #1976d2',
                    boxShadow: lesson.isUsed ? 'none' : '0 2px 8px #1976d233',
                    opacity: 0.7,
                    mb: 1,
                    width: '90%',
                    mx: 'auto',
                    minHeight: 48,
                  }}
                  onClick={() => !lesson.isUsed && handleLessonClick(col * 5 + index)}
                >
                  <CardContent sx={{ p: 1 }}>
                    <Typography variant="body1" align="center" sx={{ fontSize: 14 }}>
                      Lezione {col * 5 + index + 1}
                    </Typography>
                    {lesson.isUsed ? (
                      <Typography variant="body2" align="center" color="textSecondary" sx={{ fontSize: 12 }}>
                        {new Date(lesson.date).toLocaleDateString()}
                      </Typography>
                    ) : (
                      <Typography variant="body2" align="center" color="primary" sx={{ fontSize: 12 }}>
                        Disponibile
                      </Typography>
                    )}
                  </CardContent>
                </Card>
              ))}
            </Grid>
          ))}
        </Grid>
        {/* Nome studente in basso su barra bianca */}
        <Box sx={{
          position: 'absolute',
          left: 0,
          bottom: 0,
          width: '100%',
          bgcolor: 'rgba(255,255,255,0.95)',
          py: 2,
          zIndex: 2,
          borderTopLeftRadius: 16,
          borderTopRightRadius: 16,
        }}>
          <Typography
            variant="h5"
            align="center"
            sx={{
              fontFamily: "'Oswald', Impact, Arial, sans-serif",
              fontWeight: 700,
              letterSpacing: 2,
              color: '#1976d2',
              textShadow: '1px 1px 2px #fff',
              m: 0,
            }}
          >
            {student.name}
          </Typography>
        </Box>
        <Box sx={{ mt: 3, display: 'flex', justifyContent: 'center', zIndex: 1, position: 'relative' }}>
          <Button
            variant="outlined"
            onClick={() => window.history.back()}
          >
            Torna alla Lista
          </Button>
        </Box>
      </Paper>
    </Container>
  );
}

export default StudentCard;
